    color: #fff;
  }
  .graphiql-container .toolbar-select-options > li > svg {
    fill: #9b9b9b;
  }
  .graphiql-container .toolbar-select-options > li.hover > svg,
  .graphiql-container .toolbar-select-options > li:active > svg,
  .graphiql-container .toolbar-select-options > li:hover > svg {
