@-moz-document url-prefix("https://keylol.com/t"), url-prefix("https://keylol.com/forum.php?mod=viewthread&t") {
	/*
	 * 帖子内容
	 * post.css
	 */
	:root {
	    --simple-cubic-bezier: cubic-bezier(0, 0, .2, 1);
	    --post-title-text: var(--white1);
	    --post-breadcrumb-text: var(--white1);
	}

	/* region 主题色的背景条*/
	.floatcontainer.doc_header.wp.cl {
	    height: 0;
	}

	body:before {
	    content: '';
	    display: inline-block;
	    width: 100%;
	    min-width: 1200px;
	    height: 500px;
	    background: var(--md-block-bg);
	    position: absolute;
	    z-index: -1;
	    top: 0;
	    left: 0;
	}

	/* endregion*/

	/* region 居中部分宽度*/

	#wp, .wp {
	    --post-width: 1000px;
	    width: var(--post-width);
	    min-width: var(--post-width);
	    max-width: var(--post-width);
	}

	/* endregion*/

	/* region 帖子列表 位置 布局*/
	#postlist {
	    --post-radius: 10px;
	}

	/* region 蓝色的横带子*/
	.ad .pls,
	.ad .plc {
	    background: var(--color500);
	}

	.ad:first-child .pls,
	.ad:first-child .plc {
	    height: 0;
	}

	/* endregion*/

	/* region 圆角*/
	#postlist > div:nth-child(2) .pls[rowspan] {
	    border-top-left-radius: var(--post-radius);
