@-moz-document domain("keylol.com") {
	/*
	 * 变量配置
	 */

	/* 文字效果 */
	.subforunm_foot_intro a,
	.subforunm_foot_intro div {
	    color: var(--white1)
	}

	.subforunm_foot_banner a,
	.subforunm_foot_intro a {
	    position: relative;
	    display: inline-block;
	    overflow: hidden;
	    text-decoration: none;
	    vertical-align: top;
	    outline: 0;
	}

	.subforunm_foot_banner a:before,
	.subforunm_foot_intro a:before {
	    position: absolute;
	    top: auto;
	    bottom: 0;
	    left: 0;
	    width: 100%;
	    height: 2px;
	    content: ' ';
	    background-color: white;
	    -webkit-transition: all .2s;
	    transition: all .2s;
	    -webkit-transform: scaleX(0);
	    transform: scaleX(0);
	    -webkit-backface-visibility: hidden;
	    backface-visibility: hidden;
	}

	.subforunm_foot_banner a:focus:before,
	.subforunm_foot_banner a:hover:before,
	.subforunm_foot_intro a:focus:before,
	.subforunm_foot_intro a:hover:before {
	    -webkit-transform: scaleX(1);
	    transform: scaleX(1);
	}

	/* 布局 */

	.subforunm_foot {
	    margin-top: 60px;
	    padding-bottom: 10px;
	}

	.subforunm_foot,
	.subforunm_foot_banner {
	    background: var(--color500);
	}

	.subforunm_foot_bg:before {
	    content: '';
	    display: block;
	    position: relative;
	    top: 117px;
	    left: 20px;
	    width: 620px;
	    height: 100px;
	    background: #fff;
	    -webkit-mask-image: url(https://keylol.com/template/steamcn_metro/src/img/common/icon_with_text_256h.png);
	    -webkit-mask-size: 50%;
	    -webkit-mask-repeat: no-repeat;
	}

	.subforunm_foot_bg img {
	    opacity: 0;
	}
}

