{display:none!important}


/*1-3 主布局*/
.mn{display:flex;flex-direction:column}
a{text-decoration:none!important}
	/*1-3-1 顶栏*/
	#hd .wp{display:flex;position:fixed;top:0;width:100%;height:36px;background:var(--pri);padding:0;z-index:200}
		/*1-3-1-1 快速导航*/
		#nv{display:flex;width:auto!important;height:auto!important;position:static!important;opacity:1!important;border:0;background:0}
		#qmenu{order:0;background:0;height:auto;width:auto;line-height:34px;margin:0;padding:0 4px 0 12px;font-size:15px;white-space:nowrap;flex-shrink:0}
		#qmenu:hover{color:var(--tl)}
		#qmenu:after{content:" ▼";font-size:13px}
		#qmenu_menu{position:fixed!important;left:0!important;top:37px!important;border-color:var(--pri);box-shadow:none;width:auto;height:calc(100%)!important;padding:0}
		#qmenu_menu ul.nav a{border-radius:0;margin:0;padding:36px 0 0;background-size:46%;background-position-y:8px}
		#qmenu_menu ul.nav a:hover{background-color:var(--pri)}
		#fjump_menu{max-height:100%}
		#hd .wp > .h_pop{position:fixed!important;top:36px!important}
		.btda{border:0}
		.jump_bdl{display:flex}
		.jump_bdl .bbda{background:var(--pri);border:0}
		.jump_bdl li{background:var(--bg);border-color:var(--prid);padding:0;width:auto;min-width:150px;height:calc(100vh - 146px)}
		.jump_bdl p{padding:0 12px}
		.jump_bdl p:hover{background:var(--pri)}
		.jump_bdl p a{padding:0}
		.jump_bdl .a a, .jump_bdl .a a:hover,.jump_bdl p a:hover{background:0}
		#nv ul{display:flex;flex-grow:1;justify-content:flex-start}
		#nv li{background:none;height:auto;line-height:35px}
		#nv li a{color:var(--t);background:none!important;text-decoration:none;padding:0 8px;width:calc(100% - 16px);height:32px;text-align:center;white-space:nowrap}
		#nv li.a{background-color:var(--prid)}
		/*1-3-1-2 搜索栏*/
		#scbar{order:0;height:auto;border:0;line-height:normal;margin:0;padding:2px 4px;flex-grow:1;flex-shrink:0}
		#scbar_form>table>tbody>tr{display:flex;border:1px solid var(--ic);background-color:var(--bg);margin:2px 8px;height:26px}
			/*1-3-1-2-1 搜索类型*/
			.scbar_type_td{order:0;background:none;width:48px}
			#scbar_type{line-height:26px;padding-left:8px}
			#scbar_type:after{content:" ▼";font-size:9px;vertical-align:top}
			#scbar_type_menu{margin-top:0px;padding:0;width:48px;min-width:0;top:33px!important;border-color:var(--ic);border-top:0;box-shadow:none;margin-left:-1px}
			#scbar_type_menu li{width:48px}
			#scbar_type_menu li a{padding: 2px 0 2px 10px}
			/*1-3-1-2-2 搜索文本*/
			.scbar_txt_td{order:1;background:0}
			#scbar_txt{padding:0;background:0;width:auto;max-width:120px;height:100%;border:none;text-indent:4px}
			/*1-3-1-2-3 搜索按钮*/
			.scbar_btn_td{order:2;background:0;width:auto}
			#scbar_btn{height:27px;width:32px}
			#scbar_btn strong{display:none}
			#scbar_btn:before{display: inline-block;content: '';background:none;position: relative;bottom:2px;left:2px;width:10px;height:10px;border:2px solid var(--t);border-radius:15px;transform: rotate(140deg);transition: all .15s linear}
			#scbar_btn:hover:before{border-color:var(--sec)}
			#scbar_btn:after{display:inline-block;content:'';position: relative;right:1px;bottom:1px;width:6px;height:2.5px;background:var(--t);border-radius:2px 0 0 2px;transform:rotate(225deg);transition: all .15s linear}
			#scbar_btn:hover:after{background:var(--sec)}
		/*1-3-1-3 顶栏用户信息*/
		.hdc.cl{order:1;min-height:auto;width:auto;margin:0;flex-shrink:0;overflow:hidden}
		.hdc.cl:after{display:none}
		#um{display:flex;margin:0;padding:0 12px}
		#um > p{display:flex;line-height:35px;flex-wrap:nowrap}
		#um > p > a,#um:hover #loginstatus,#um:hover #loginstatus,#um:hover a[href="home.php?mod=spacecp"],#um:hover a[href^="member.php?mod=logging&action=logout"],#um:hover .vwmy{margin-right:8px;font-size:13px;white-space:nowrap}
		.vwmy{order:-2;background:0;padding:0;margin:0;font-size:0}
		#um a[href="home.php?mod=spacecp"],#um #loginstatus,#um #loginstatus{order:-1;margin:0;font-size:0}
		#um a[href^="member.php?mod=logging&action=logout"]{order:-2;margin:0;font-size:0}
		#um .avt{order:1;z-index:10;float:none;margin:3px 0 0 8px}
		#um .avt img{height:26px;width:26px;border:0}
			/*1-3-1-3-2 消息提醒*/
			#myprompt.new,#pm_ntc.new{background:0;padding:0;color:var(--secp)!important}
			#myprompt.new:before{display:inline-block;content:"💡";padding:0 4px;font-size:14px}
			#pm_ntc.new:before{display:inline-block;content:"✉️";padding:0 4px;font-size:14px;line-height:20px}
			.prompt_news, .prompt_follower, .prompt_news_0, .prompt_follower_0, .ignore_notice, .prompt_concern, .notice_interactive, .notice_pm, .notice_system, .notice_manage, .notice_app, .notice_mypost{background:0;height:auto;margin:auto 2px auto 0;text-align:center}
			.notice_pm:before, .prompt_news:before,.prompt_news_0:before{content:"✉";font-size:16px;line-height:16px}
			.notice_interactive:before,.prompt_follower:before,.prompt_follower_0:before{content:"🎔";font-size:16px;line-height:14px}
			.notice_mypost:before{content:"🗎";font-size:20px;line-height:12px}
			.notice_system:before{content:"🕭";font-size:17px}
			.p_pop .notice_system:before{line-height:12px}
			.notice_app:before{content:"❖";font-size:18px}
			/*1-3-1-3-1 登录*/
			.fastlg{padding:0 12px!important;height:36px;line-height:32px}
			.fastlg tbody{display:flex;padding:0}
			.fastlg button.pn{width:46px!important}
			.fastlg_l{border:0}
			#messagelogin{border-top:0}
		/*1-3-1-4 地址栏*/
		#pt{position:fixed;top:36px;left:0;width:100%;background-color:var(--pril);margin:0;padding-left:12px;height:30px;line-height:26px;box-shadow:0 0 3px var(--shd);z-index:198}
		#pt .z{display:flex;font-size:14px;align-items:center}
		#pt .z a, #pt .z em, #pt .z span{height:25px;font-size:14px;line-height:25px;border:1px solid transparent;border-width:2px 1px;padding:0 3px;text-decoration:none}
		#pt .z em{background:0;font-size:0;width:auto}
		#pt .z em:before{content:"›";color:var(--tg);background:0;font-size:14px;font-weight:bold}
		#pt .z a:hover,#pt .z a:hover+em{color:var(--pt)!important;background-color:var(--pri);border-color:var(--pril)}
		#pt .z a:hover+em:before{color:var(--pt)}
		.nvhm{display:flex;background:0;width:18px;line-height:normal;height:25px!important;font-size:0!important;text-decoration:none!important}
		.nvhm:before{display:flex;content:"1";background:var(--icl);color:var(--icddd);font-family:黑体;font-size:13px;font-weight:bold;margin:3px 0 0;line-height:13px;height:13px;min-width:13px;border:4px solid var(--icddd);border-radius:16px;justify-content:center}
		/*1-3-1-5 页码栏*/
		#pgt{display:flex!important;flex-direction:row-reverse;position:fixed;top:36px;right:0;padding:3px 12px;margin:0!important;z-index:199}
		#pgt .pg, #pgt .pgb{margin:0}
		.pg a, .pg strong, .pgb a, .pg label{height:22px;line-height:22px;border-color:var(--prid);color:var(--t)}
		.pg strong{background-color:var(--prid)}
		.pg a, .pgb a, .pg label{background-color:var(--pril)}
		.pg a:hover, .pgb a:hover{border-color:var(--prid);background-color:var(--pri);box-shadow:inset 0 1px 2px var(--shdl);line-height:25px}
		.pg a:active, .pgb a:active{border-color:var(--prid);background-color:var(--pri);box-shadow:inset 0 2px 3px var(--shd);line-height:26px}
		.pg label .px{text-align:center;border-color:var(--prid);width:auto}
		.pgb a{background:0;padding:0 8px}
		.pg a.prev:hover,.pg a.nxt:hover{background-position-y:70%}
		.pg a.prev:active,.pg a.nxt:active{background-position-y:90%}
		#darkroommore{background-image:none;padding-right:8px}
		#darkroommore:after{content:" ▷"}
		#visitedforums_menu{position:fixed!important;width:auto;top:64px!important}
		#visitedforums_menu a:hover{background:0;color:var(--tl)!important}
		#visitedforums_menu td{padding:0}
		#v_forums h3{padding:2px 8px!important;margin:0!important;background-color:var(--pri);border:0}
		#v_forums li{background:0;padding:0 8px}
			/*1-3-1-5-1 发帖回帖按钮*/
			#newspecial img, #post_reply img,#pgt a[href="forum.php?mod=misc&action=nav"] img{display:none}
			#newspecial, #post_reply,#pgt a[href="forum.php?mod=misc&action=nav"]{background-color:var(--bnd);background-image:linear-gradient(to bottom,var(--bn),var(--bnd));background-position: 0;background-repeat:no-repeat;border:1px solid;border-color:#00000019 #00000019 #0000003f;border-radius:2px;box-shadow:inset 0 1px 0 #ffffff33,0 1px 2px #0000000c;color:#ddd!important;height:22px;line-height:22px;font-size:13px;font-weight:bold;text-shadow:1px 1px 1px var(--shd);transition:all .15s linear;float:none;margin:0 0 0 4px!important}
			#newspecial:hover, #post_reply:hover,#pgt a[href="forum.php?mod=misc&action=nav"]:hover{background-position: 0 -12px;color:white!important;text-decoration: none}
			#newspecial:before{padding-left:10px;content:"发帖"}
			#newspecial:after{padding:0 8px 0 4px;content:"▼";font-size:10px;vertical-align: top}
			#post_reply:before{padding:0 8px;content:"回帖"}
			#pgt a[href="forum.php?mod=misc&action=nav"]:before{padding:0 10px;content:"发帖"}
				/*1-3-1-5-1-1 发帖类型菜单*/
				#newspecial_menu{background-color:var(--bnd);background-image:linear-gradient(to bottom,var(--bn),var(--bnd));background-repeat:no-repeat;border:1px solid;border-color:#00000019 #00000019 #0000003f;border-radius:2px;box-shadow:inset 0 1px 0 #ffffff33,0 1px 2px #0000000c;color:#ddd!important;font-size:13px;font-weight:bold;text-shadow:1px 1px 1px var(--shd);transition-duration:.15s;transition-timing-function:linear; position:fixed!important; top:64px!important;width:auto;padding:3px 8px}
				#newspecial_menu li{display:flex;background:0!important;align-items:center;justify-content:center;text-shadow:none}
				#newspecial_menu li a{border:0;color:#ddd!important;padding:1px 0}
				#newspecial_menu li a:hover{color:white!important;text-decoration:none;text-shadow:1px 1px 1px var(--shd)}
				#newspecial_menu .poll:before, #sti .poll:before{content:"📊";margin-right:4px}
				#newspecial_menu .trade:before, #sti .trade:before{content:"🛒";margin-right:4px}
				#newspecial_menu .debate:before, #sti .debate:before{content:"💥";margin-right:4px}
/*1-4 通用内容*/
body{background-color:var(--bg)!important;color:var(--t)}
body>#wp{margin-top:80px;padding:0 12px}
#hd .wp, #wp{min-width:510px}
.wp{width:auto}
.bm_h{background-color:var(--pri);border:0}
.bm, .bn{margin:0}
	/*1-4-1 统一字色*/input, button, select, textarea,.tl td em, .tl td em a,.xi2, .xi2 a, .xi3 a,.xg2,.tfm .d,.pbw p,.tattl dd{color:var(--t)}
	a:not([style*="color:"]),.pg_index a,.tps a,.xg1, .xg1 a,.xg1 .xi2,.pinf,.pinf strong{color:var(--t)!important}
