/*4-5-1 自动收纳用户信息 风格1*/

		#postlist .favatar{overflow:hidden;max-height:168px}
		#postlist .favatar .tns.xg2{opacity:0;transition-duration:.35s}
		#postlist .favatar .tns.xg2+p{width:90px;opacity:0;transition-duration:.3s}
		#postlist .favatar .xg1{display:block!important;width:120px;opacity:0;transition-duration:.25s}
		#postlist .favatar .pil{opacity:0;transition-duration:.2s}
		#postlist .favatar .o{width:auto;opacity:0;transition-duration:.15s}
		#postlist .pls:hover .favatar{max-height:100%;max-height:500px}
		#postlist .pls:hover .favatar .tns.xg2{opacity:1;transition-duration:.15s}
		#postlist .pls:hover .favatar .tns.xg2+p{opacity:1;transition-duration:.2s}
		#postlist .pls:hover .favatar .xg1{opacity:1;transition-duration:.25s}
		#postlist .pls:hover .favatar .pil{opacity:1;transition-duration:.3s}
		#postlist .pls:hover .favatar .o{opacity:1;transition-duration:.35s}/**/
		/*4-5-2 自动收纳用户信息 风格2*/
